import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import useImageHook from '../hooks/imageHook';
import ImageTile from './imageTile';
import {getImages} from '../Api';
import FullscreenImage from './fullscreenImage';

const StyledGridContainer = styled.div`
  width: 100%;
  height: 100%;
  padding: 5px;
`;

const StyledGrid = styled.div`
width: 100%;
@media screen and (min-width: 1000px) {
  width: 1200px;
  margin: 0 auto;
}
`;

const Grid = () => {
  const [images, setImages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [hasInitialized, setHasInitialized] = useState(false);
  const [showFullscreen, setShowFullscreen] = useState(false);
  const [fullscreenImageObject, setFullscreenImageObject] = useState();

  const { init, imageObjects, getImageObjects } = useImageHook();

  useEffect(() => {
    if (hasInitialized && imageObjects.length) setImages(imageObjects);
  }, [hasInitialized, imageObjects]);

  useEffect(() => {
    if (!hasInitialized) {
      init();
      setHasInitialized(true);
    }
  }, [hasInitialized]);


  const handleGetImages = async () => {
    const newImages = await getImages(currentPage);
    console.log('-- IMAGES;', images)
    setImages(images.concat(newImages));
  }

  const handleScroll = (e) => {
    const bottom = e.target.scrollHeight - e.target.scrollTop === e.target.clientHeight;

    if (bottom) {
      handleGetImages
    }
  }

  const handleShowFullscreenImage = (imageObj) => {
    setFullscreenImageObject(imageObj);
    setShowFullscreen(true);
  }

  const handleCloseFullscreenImage = () => {
    setShowFullscreen(false);
  }

  return (
    <StyledGridContainer onScroll={handleScroll}>
      <StyledGrid>
        {images.map(image => <ImageTile imageData={image} onClick={handleShowFullscreenImage} />)}
      </StyledGrid>
      {showFullscreen && <FullscreenImage imageData={fullscreenImageObject} onClose={handleCloseFullscreenImage} />}
    </StyledGridContainer>
  )

}

export default Grid;
