import React, { useEffect } from 'react';
import styled from 'styled-components';

const StyledFullScreenImageContainer = styled.div`
  height: 100vh;
  width: 100vw;
  background: rgb(0,0,0,0.5);
`;

const FullscreenImage = (props) => {
  const { imageData } = props;


  const { getFullscreenImage } = useImageHook();

  useEffect(() => {
    if (imageData) {
      getFullscreenImage(imageData.urls.full);
    }
  }, [imageData])

  return (
    <StyledFullScreenImageContainer><img src={imageData.urls.full} /></StyledFullScreenImageContainer>
  );
}

export default FullscreenImage;
