import React from 'react';
import useImageHook from '../hooks/imageHook';

const Control = (props) => {
  const {direction, onControlClick} = props;
  const { getNextImage, getPrevImage } = useImageHook();

  const controlFunction = direction === "next" ? getNextImage : getPrevImage;

  return (
    <div onClick={controlFunction}>{direction}</div>
  )

}

export default Control;
