import React, { useEffect, useState, useCallback } from 'react';
import styled from 'styled-components';
import Control from './control';

const StyledFullScreenImageContainer = styled.div`
  position: absolute;
  top: 0;
  height: 100%;
  width: 100%;
  text-align: center;
  background: rgb(0,0,0,0.8);
`;

const StyledFullscreenImage = styled.img`
  max-height: 90%;  
  max-width: 90%;
  margin: 20px 50px 50px 50px;
`;

const StyledControlBar = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 20px;
`;

const StyledCloseButton = styled.div`
  display: block;
  color: white;
  cursor: pointer;
  margin: 10px;
`;

const FullscreenImage = (props) => {
  const { imageObjects, currentImageIndex, onCloseClick, onNextClick, onPrevClick } = props;

  const [imageIndex, setImageIndex] = useState(currentImageIndex);
  const [currentImage, setCurrentImage] = useState(imageObjects[currentImageIndex]);

  const handlePrevClick = useCallback(() => {
    setImageIndex(imageIndex - 1);
    setCurrentImage(imageObjects[imageIndex - 1]);
  }, [setImageIndex, imageIndex, setCurrentImage])

  const handleNextClick = useCallback(() => {
    setImageIndex(imageIndex + 1);
    setCurrentImage(imageObjects[imageIndex + 1])
  }, [setImageIndex, imageIndex, setCurrentImage])

  return (
    <StyledFullScreenImageContainer>
      <StyledControlBar>
        <Control onControlClick={handlePrevClick}>Prev</Control>
        <StyledCloseButton onClick={onCloseClick}>Close</StyledCloseButton>
        <Control onControlClick={handleNextClick}>Next</Control>
      </StyledControlBar>
      {currentImage && <StyledFullscreenImage src={currentImage.urls.regular} />}
    </StyledFullScreenImageContainer>
  );
}

export default FullscreenImage;
