import React, { useEffect } from 'react';
import styled from 'styled-components';
import Control from './control';
import useImageHook from '../hooks/imageHook';

const StyledFullScreenImageContainer = styled.div`
  position: absolute;
  top: 0;
  height: 100%;
  width: 100%;
  text-align: center;
  background: rgb(0,0,0,0.8);
`;

const StyledFullscreenImage = styled.img`
  max-height: 90%;  
  max-width: 90%;
  margin: 20px 50px 50px 50px;
`;

const StyledControlBar = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 20px;
`;

const StyledCloseButton = styled.div`
  display: block;
  color: white;
  cursor: pointer;
  margin: 10px;
`;

const FullscreenImage = (props) => {
  const { index, imageData, onClose, onNextClick, onPrevClick } = props;

  const handleControlClick = (direction) => {
    const controlFunction = direction === "next" ? getNextImageObject : getPrevImageObject;

    controlFunction(index);
  }


  return (
    <StyledFullScreenImageContainer onClick={onClose}>
      <StyledControlBar>
        <Control onControlClick={onPrevClick}>Prev</Control>
        <StyledCloseButton onClick={onClose}>Close</StyledCloseButton>
        <Control onControlClick={onNextClick}>Next</Control>
      </StyledControlBar>
      <StyledFullscreenImage src={imageData.urls.regular} />
    </StyledFullScreenImageContainer>
  );
}

export default FullscreenImage;
