import React from 'react';
import styled from 'styled-components';
import useImageHook from '../hooks/imageHook';

const StyledControl = styled.button`
  padding: 10px;
  cursor: pointer;
`;

const Control = (props) => {
  const {direction, onControlClick} = props;
  const { getNextImageObject, getPrevImageObject } = useImageHook();

  const controlFunction = direction === "next" ? getNextImageObject : getPrevImageObject;

  return (
    <StyledControl onClick={controlFunction}>{direction}</StyledControl>
  )

}

export default Control;
