import React, { useEffect } from 'react';
import styled from 'styled-components';
import Control from './control';

const StyledFullScreenImageContainer = styled.div`
  position: absolute;
  top: 0;
  height: 100%;
  width: 100%;
  text-align: center;
  background: rgb(0,0,0,0.8);
`;

const StyledFullscreenImage = styled.img`
  max-height: 90%;  
  max-width: 90%;
  margin: 20px 50px 50px 50px;
`;

const StyledControlBar = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 20px;
`;

const StyledCloseButton = styled.div`
  display: block;
  color: white;
  cursor: pointer;
  margin: 10px;
`;

const FullscreenImage = (props) => {
  const { images, currentIndex, onClose, onNextClick, onPrevClick } = props;

  const [imageIndex, setImageIndex] = useState(currentIndex);

  const handlePrevClick = () => {
    setImageIndex(imageIndex - 1);
  }

  const handleNextClick = () => {
    setImageIndex(imageIndex + 1);
  }

  return (
    <StyledFullScreenImageContainer onClick={onClose}>
      <StyledControlBar>
        <Control onControlClick={onPrevClick}>Prev</Control>
        <StyledCloseButton onClick={onClose}>Close</StyledCloseButton>
        <Control onControlClick={onNextClick}>Next</Control>
      </StyledControlBar>
      <StyledFullscreenImage src={images[imageIndex].urls.regular} />
    </StyledFullScreenImageContainer>
  );
}

export default FullscreenImage;
