import axios from 'axios';
import { imageTypes, getImageUrls } from './utils';

const ACCESS_KEY = 'rkcVhVCZfLEaTGKdJar6pLrHvR9QC9Mn2a6R8Yk7I1g';
const AUTH_HEADER = { Authorization: `Client-ID ${ACCESS_KEY}` };
const BASE_URL = 'https://api.unsplash.com/photos';
const AUTH_PARAM = `?client_id=${ACCESS_KEY}`        

export const getImages = async (page) => {
  try {
    let images = [];
    const imagesList = await axios.get(`${BASE_URL}`, { headers: AUTH_HEADER });

    // TODO: move this out
    if (imagesList.data.length) {
      const imageUrls = getImageUrls(imagesList.data, imageTypes.THUMB);

      images = getThumbnails(imageUrls);
    }

    return images;
  } catch(err) {
    throw err;
  }
};

const getThumbnails = async (imageUrls) => {
  const getImagePromises = imageUrls.map(imageUrl => getImage(imageUrl));

  let results = await Promise.all(getImagePromises);

  return results;
}

export const getImage = async (url) => {
  try {
    return await axios.get(url, { headers: AUTH_HEADER });
  } catch(err) {
    throw err;
  }
};
